const navSlide=()=>{const burger=document.querySelector(".burger"),nav=document.querySelector(".nav-links"),navLinks=document.querySelectorAll(".nav-links li");burger.addEventListener("click",()=>{nav.classList.toggle("nav-active"),navLinks.forEach((link,index)=>{link.style.animation?link.style.animation="":link.style.animation=`navLinkFade 0.5s ease forwards ${index/7+.3}s`}),burger.classList.toggle("toggle")}),nav.addEventListener("click",()=>{nav.classList.toggle("nav-active"),burger.classList.toggle("toggle"),navLinks.forEach((link,index)=>{link.style.animation&&(link.style.animation="")})})};function scrollFunction(){const nav=document.getElementById("nav"),logo=document.querySelector(".logo");document.body.scrollTop>20||document.documentElement.scrollTop>20?(nav.style.backgroundColor="rgb(6, 82, 70)",nav.style.boxShadow="0px 2px 4px rgba(0, 0, 0, 0.18)",logo.style.textShadow="0px 0px 0px transparent"):(nav.style.backgroundColor="transparent",nav.style.boxShadow="0px 0px 0px rgba(0, 0, 0, 0)",logo.style.textShadow="1px 1px 5px white")}navSlide(),window.onscroll=function(){scrollFunction()};